mapper.insert:
	INSERT INTO BOOK 
	   ( BOOK_SEQ
	   , ISBN
	   , TITLE
	   , AUTHOR
	   , CONTENT
	   , COMPANY_CD
	   , TOTAL_PAGE
	   , PRICE
	   , QUANTITY
	   , REG_ID)
    VALUES 
       ( SEQ_BOOK.NEXTVAL
       , #{isbn}
       , #{title}
       , #{author}
       , #{content}
       , #{companyCd}
       , #{totalPage}
       , #{price}
       , #{quantity}
       , #{regId})

mapper.update:
UPDATE BOOK b
   SET b.isbn = #{isbn}
     , b.title = #{title}
     , b.author = #{author}
     , b.content = #{content}
     , b.company_cd = #{companyCd}
     , b.total_page = #{totalPage}
     , b.price = #{price}
     , b.quantity = #{quantity}
     , b.mod_id = #{modId} 
     , b.mod_date = SYSDATE
 WHERE b.book_seq = #{bookSeq}

mapper.select:
SELECT b.book_seq
  FROM book b
 WHERE b.book_seq = #{bookSeq}
